import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';  // To get the itinerary ID from URL
import { db } from '../firebase/firebase';  // Firebase Firestore setup
import { doc, getDoc } from 'firebase/firestore';  // Firestore functions

const ItineraryDetails = () => {
  const { id } = useParams();  // Retrieve the itinerary ID from URL
  const [itinerary, setItinerary] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchItineraryDetails = async () => {
      try {
        // Fetch itinerary by ID from Firestore
        const docRef = doc(db, 'itineraries', id);
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          setItinerary(docSnap.data());
        } else {
          setError('No itinerary found!');
        }
        setLoading(false);
      } catch (err) {
        setError('Failed to load itinerary details.');
        setLoading(false);
      }
    };

    fetchItineraryDetails();
  }, [id]); // Trigger the effect when `id` changes

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>Itinerary Details</h2>
      {itinerary && (
        <div>
          <h3>{itinerary.destination}</h3>
          <p><strong>Activity:</strong> {itinerary.activity}</p>
          <p><strong>Date:</strong> {itinerary.date}</p>
          <p><strong>Description:</strong> {itinerary.description}</p>
          <img src={itinerary.photoURL} alt={itinerary.destination} width="300" />
        </div>
      )}
    </div>
  );
};

export default ItineraryDetails;
