// AddItinerary.js

import React, { useState } from "react";
import { db } from "../firebase/firebase"; // Import Firestore service
import { collection, addDoc } from "firebase/firestore"; // Firestore methods for adding documents

const AddItinerary = () => {
  const [destination, setDestination] = useState("");
  const [activity, setActivity] = useState("");
  const [dates, setDates] = useState("");
  const [photos, setPhotos] = useState("");
  const [tripType, setTripType] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      // Add itinerary to Firestore
      await addDoc(collection(db, "itineraries"), {
        destination,
        activity,
        dates,
        photos,
        tripType,
      });
      setLoading(false);
      console.log("Itinerary added successfully");
    } catch (error) {
      console.error("Error adding itinerary: ", error);
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Add New Itinerary</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Destination:</label>
          <input
            type="text"
            value={destination}
            onChange={(e) => setDestination(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Activity:</label>
          <input
            type="text"
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Dates:</label>
          <input
            type="text"
            value={dates}
            onChange={(e) => setDates(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Photos:</label>
          <input
            type="text"
            value={photos}
            onChange={(e) => setPhotos(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Trip Type:</label>
          <select
            value={tripType}
            onChange={(e) => setTripType(e.target.value)}
            required
          >
            <option value="">Select Trip Type</option>
            <option value="adventure">Adventure</option>
            <option value="leisure">Leisure</option>
            <option value="work">Work</option>
          </select>
        </div>
        <button type="submit" disabled={loading}>
          {loading ? "Adding..." : "Add Itinerary"}
        </button>
      </form>
    </div>
  );
};

export default AddItinerary;
