import React, { useState, useEffect } from "react";
import { db } from "../firebase";
import { collection, addDoc, getDocs } from "firebase/firestore";

const Itineraries = () => {
  const [itineraries, setItineraries] = useState([]);
  const [newItinerary, setNewItinerary] = useState({ destination: "", activity: "" });

  const fetchItineraries = async () => {
    const querySnapshot = await getDocs(collection(db, "itineraries"));
    const data = querySnapshot.docs.map(doc => ({ ...doc.data(), id: doc.id }));
    setItineraries(data);
  };

  const addItinerary = async () => {
    await addDoc(collection(db, "itineraries"), newItinerary);
    fetchItineraries();
  };

  useEffect(() => {
    fetchItineraries();
  }, []);

  return (
    <div className="itineraries-container">
      <h1>Itineraries</h1>
      <input
        type="text"
        placeholder="Destination"
        value={newItinerary.destination}
        onChange={(e) => setNewItinerary({ ...newItinerary, destination: e.target.value })}
      />
      <input
        type="text"
        placeholder="Activity"
        value={newItinerary.activity}
        onChange={(e) => setNewItinerary({ ...newItinerary, activity: e.target.value })}
      />
      <button onClick={addItinerary}>Add Itinerary</button>
      <ul>
        {itineraries.map((itinerary) => (
          <li key={itinerary.id}>
            {itinerary.destination} - {itinerary.activity}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Itineraries;
