// src/pages/Dashboard.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { db, auth } from '../firebase';
import { collection, addDoc, getDocs } from 'firebase/firestore';

const Dashboard = () => {
  const [itineraries, setItineraries] = useState([]);
  const [destination, setDestination] = useState('');
  const [activity, setActivity] = useState('');
  
  useEffect(() => {
    const fetchItineraries = async () => {
      const querySnapshot = await getDocs(collection(db, "itineraries"));
      const itineraryList = querySnapshot.docs.map(doc => doc.data());
      setItineraries(itineraryList);
    };
    fetchItineraries();
  }, []);

  const addItinerary = async () => {
    try {
      await addDoc(collection(db, "itineraries"), { destination, activity });
      setDestination('');
      setActivity('');
    } catch (error) {
      console.error("Error adding itinerary: ", error);
    }
  };

  return (
    <div>
      <h2>Welcome to your Dashboard</h2>
      <div>
        <h3>Add Itinerary</h3>
        <input type="text" placeholder="Destination" value={destination} onChange={(e) => setDestination(e.target.value)} />
        <input type="text" placeholder="Activity" value={activity} onChange={(e) => setActivity(e.target.value)} />
        <button onClick={addItinerary}>Add Itinerary</button>
      </div>
      
      <h3>Your Itineraries</h3>
      {itineraries.map((itinerary, index) => (
        <div key={index}>
          <h4>{itinerary.destination}</h4>
          <p>{itinerary.activity}</p>
        </div>
      ))}
    </div>
  );
};

export default Dashboard;
