// src/pages/Itinerary.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { db } from '../firebase';
import { doc, getDoc } from 'firebase/firestore';

const Itinerary = () => {
  const { id } = useParams(); // Get itinerary id from URL params
  const [itinerary, setItinerary] = useState(null);

  useEffect(() => {
    const fetchItinerary = async () => {
      try {
        const docRef = doc(db, "itineraries", id); // Reference to the specific itinerary
        const docSnap = await getDoc(docRef);

        if (docSnap.exists()) {
          setItinerary(docSnap.data());
        } else {
          console.log("No such document!");
        }
      } catch (error) {
        console.error("Error fetching itinerary:", error);
      }
    };

    if (id) {
      fetchItinerary();
    }
  }, [id]);

  if (!itinerary) return <div>Loading...</div>;

  return (
    <div className="itinerary-detail">
      <h2>{itinerary.destination}</h2>
      <p><strong>Activity:</strong> {itinerary.activity}</p>
      <p><strong>Trip Type:</strong> {itinerary.type}</p>
      <p><strong>Date:</strong> {itinerary.date}</p>
      <div>
        <h3>Photos</h3>
        <div className="photo-gallery">
          {itinerary.photos.map((url, index) => (
            <img key={index} src={url} alt={`Photo ${index + 1}`} />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Itinerary;
